export default {
    get:function (name) {
        if (document.cookie.length > 0) {
            let start = document.cookie.indexOf(name + '=');
            if (start !== -1) {
                start = start + name.length + 1;
                let end = document.cookie.indexOf(";", start);
                if (end === -1) end = document.cookie.length;
                return decodeURIComponent(document.cookie.substring(start, end));
            }
        }
        return null;
    },

    set:function (name, value, expireSecond) {
        let timestamp = Date.parse(new Date()) + expireSecond*1000;
        let expireDate = new Date(timestamp);
        document.cookie = name + "=" + encodeURIComponent(value) + ((expireSecond === null) ? "" : ";expires=" + expireDate.toUTCString());
    },

    del:function (name) {
        this.set(name, '', -1);
    }

}