<!-- 实体类详情用表格形式显示 -->
<template>
    <el-table
            :data="data"
            v-bind:showHeader="false"
            border
            stripe
            style="width: 100%;">
        <el-table-column prop="label" :width="labelWidth" align="right"></el-table-column>
        <el-table-column prop="value" align="left">
            <template slot-scope="scope">
                <slot v-if="scope.row.slot" :name="scope.row.slot" :value="scope.row.value">{{scope.row.value}}</slot>
                <template v-else>{{scope.row.value}}</template>
            </template>
        </el-table-column>
    </el-table>
</template>
<script>
    export default {
        name: 'BaseDetailTable',
        props: {
            labelWidth: {
                type: Number,
                default: 200
            },
            data: {
                type: Array,
                default: [
                    // {label:'订单号', value:'12123',slot:'可空'}
                ]
            }
        }
    }
</script>
