function Logger(debug) {

    this.debug = debug || false;

    this.info = function (msg) {
        if (this.debug) console.log(msg);
    };

    this.warn = function (msg) {
        if (this.debug) console.warn(msg);
    };

    //错误信息需要收集到服务器
    this.error = function (msg) {
        console.error(msg);
    };

}

export default Logger;

