/**
 * 定义常用的枚举
 */
let enumPlugin = {}
enumPlugin.install = function (Vue, options) {
    Vue.prototype.$enum = {
        yesNo: {
            0: '否',
            1: '是'
        },
        openClose: {
            0: '关闭',
            1: '开启'
        }
    }
}
export default enumPlugin
