<template>
    <el-table
            :data="list"
            border
            style="width: 100%">
        <slot name="extend"></slot>

        <el-table-column prop="payChannel" label="渠道" header-align="center" align="center">
            <template slot-scope="scope">
                快付通
            </template>
        </el-table-column>

        <el-table-column prop="chnlOrderNo" label="流水号" header-align="center" align="right">
        </el-table-column>


        <el-table-column prop="billType" label="业务类型" header-align="center" align="center">
            <template slot-scope="scope">
                {{$getObjVal(billTypeMap, scope.row.billType)}}
            </template>
        </el-table-column>

        <el-table-column prop="billMoney" label="账单金额(元)" align="right" header-align="center">
            <template slot-scope="scope">
                {{$moneyFormat(scope.row.billMoney)}}
            </template>
        </el-table-column>

        <el-table-column prop="balance" label="当前余额(元)" align="right" header-align="center">
            <template slot-scope="scope">
                {{$moneyFormat(scope.row.balance)}}
            </template>
        </el-table-column>

        <el-table-column prop="billTime" label="账单时间" align="center" header-align="center">
            <template slot-scope="scope">
                {{$mTimeToDate(scope.row.billTime)}}
            </template>
        </el-table-column>
    </el-table>
</template>
<script>
    import BillTypeEnum from '../../enum/BillTypeEnum'

    export default {
        name: 'ChnlBillListTable',
        data () {
            return {
                billTypeMap: BillTypeEnum.map
            }
        },
        props: {
            list: Array
        }
    }
</script>