<!--手续费充值-table-->
<template>
  <el-table
    :data="list"
    border
    style="width: 100%">
    <slot name="extend"></slot>
    <el-table-column label="创建日期" header-align="center" align="center"
                     width="155">
      <template slot-scope="scope">
        {{$mTimeToDate(scope.row.createdAt)}}
      </template>
    </el-table-column>
    <el-table-column prop="rechargeNo" label="流水号" header-align="center" align="center"
                     width="200"></el-table-column>
    <el-table-column label="充值金额(元)" align="right" header-align="center" width="150">
      <template slot-scope="scope">
        {{$moneyFormat(scope.row.rechargeMoney)}}
      </template>
    </el-table-column>
    <el-table-column prop="cardNo" label="出款银行账户" header-align="center" align="center"
                     width="200"></el-table-column>
    <el-table-column label="出款银行" header-align="center" align="center"
                     width="100">
      <template slot-scope="scope">
        {{$getObjVal(bankNos, scope.row.bankNo)}}
      </template>
    </el-table-column>
    <el-table-column prop="payeeCardNo" label="收款银行帐号" header-align="center" align="center"
                     width="200"></el-table-column>
    <el-table-column label="充值状态" header-align="center" width="90" align="center">
      <template slot-scope="scope">
        {{$getObjVal(tradeStatusEnum.map, scope.row.rechargeStatus)}}
      </template>
    </el-table-column>
    <el-table-column prop="note" label="备注" header-align="center"
                    ></el-table-column>
  </el-table>
</template>
<script>
  import bankNos from 'bluesdk-bank-no'
  import TradeStatusEnum from './../../enum/TradeStatusEnum'

  export default {
    name: 'ChargeFeeListTable',
    data () {
      return {
        tradeStatusEnum: TradeStatusEnum,
        bankNos: bankNos
      }
    },
    props: {
      list: Array
    }
  }
</script>
