<!--手续费充值-form-->
<template>
    <el-form :inline="true" class="query-form">
        <date-range :begin-value="dateBegin"
                    :end-value="dateEnd"
                    :common-range="commonDateRange"
                    @updateBeginValue="val=>this.$emit('update:dateBegin',val)"
                    @updateEndValue="val=>this.$emit('update:dateEnd',val)"
                    @updateCommonRange="val=>this.$emit('update:commonDateRange',val)"
        ></date-range>
        <slot name="extend"></slot>

        <el-form-item label="交易状态" prop="sucFlag">
            <enum-select :options="tradeStatusEnum"
                         :show-empty="true"
                         :selected-id="sucFlag"
                         @change="val=>this.$emit('update:sucFlag', val)" style="width: 130px;"></enum-select>
        </el-form-item>


        <el-form-item>
            <el-button type="primary" @click="search()">查询</el-button>
        </el-form-item>
        <!--立即充值按钮-->
        <slot name="rechargeBtn"></slot>
    </el-form>


</template>
<script>
    import {EnumSelect, DateRange} from 'bluesdk-element-common'
    import TradeStatusEnum from './../../enum/TradeStatusEnum'

    export default {
        name: 'ChargeFeeSearchForm',
        data () {
            return {
                tradeStatusEnum: TradeStatusEnum.map
            }
        },
        props: {
            'dateBegin': String,
            'dateEnd': String,
            'commonDateRange': String,
            'sucFlag': String
        },
        components: {DateRange, EnumSelect},
        methods: {
            search () {
                this.$emit('search')
            }
        }
    }
</script>
