<template>
    <el-table
            :data="list"
            border
            style="width: 100%">

        <slot name="extend"></slot>

        <el-table-column prop="outTradeNo" label="报关流水号" align="center"></el-table-column>
        <el-table-column prop="payOrderNo" label="支付流水号" align="center"></el-table-column>
        <el-table-column prop="oriMchOrder" label="商户订单号" align="center"></el-table-column>
        <el-table-column prop="customType" label="海关通道" align="center" width="120"></el-table-column>
        <el-table-column prop="customCode" label="海关编码" align="center"
                         width="80"></el-table-column>
        <el-table-column prop="ciqCode" label="国检编码" align="center"
                         width="80"></el-table-column>


        <el-table-column label="报关状态" align="center" width="80">
            <template slot-scope="scope">
                {{$getObjVal(CustomStatusEnum.map, scope.row.customStatus)}}
            </template>
        </el-table-column>

        <el-table-column label="报检状态" align="center" width="80">
            <template slot-scope="scope">
                {{$getObjVal(CustomStatusEnum.map, scope.row.ciqStatus)}}
            </template>
        </el-table-column>
        <el-table-column prop="retMsg" label="回执" header-align="center" align="left"></el-table-column>
        <el-table-column label="发起时间" align="center" width="160">
            <template slot-scope="scope">
                {{$mTimeToDate(scope.row.createdAt)}}
            </template>
        </el-table-column>
        <el-table-column label="更新时间" align="center" width="160">
            <template slot-scope="scope">
                {{$mTimeToDate(scope.row.updatedAt)}}
            </template>
        </el-table-column>

        <el-table-column label="详情" align="center" width="85">
            <template slot-scope="scope">
                <router-link class="el-button el-button--default"
                             :to="{ name: 'customView', params: { reportId: scope.row.reportId }}">
                    详情
                </router-link>
            </template>
        </el-table-column>
    </el-table>
</template>
<script>
    import CustomStatusEnum from '../../enum/CustomStatusEnum'

    export default {
        name: 'CustomOrderListTable',
        data () {
            return {
                CustomStatusEnum: CustomStatusEnum
            }
        },
        props: {
            list: Array
        }
    }
</script>