<template>
    <el-form :inline="true" class="query-form">
        <date-range :begin-value="dateBegin"
                    :end-value="dateEnd"
                    :common-range="commonDateRange"
                    @updateBeginValue="val=>this.$emit('update:dateBegin',val)"
                    @updateEndValue="val=>this.$emit('update:dateEnd',val)"
                    @updateCommonRange="val=>this.$emit('update:commonDateRange',val)"
        ></date-range>
        <slot name="extend"></slot>

        <el-form-item label="报关流水号" prop="outTradeNo">
            <el-input name="outTradeNo" id="outTradeNo" placeholder="报关流水号"
                      @change="val=>this.$emit('update:outTradeNo', val)"
                      :value="outTradeNo"
            ></el-input>
        </el-form-item>

        <el-form-item label="支付流水号" prop="payOrderNo">
            <el-input name="payOrderNo" id="payOrderNo" placeholder="支付流水号"
                      @change="val=>this.$emit('update:payOrderNo', val)"
                      :value="payOrderNo"
            ></el-input>
        </el-form-item>

        <el-form-item label="商户订单号" prop="oriMchOrder">
            <el-input name="oriMchOrder" id="oriMchOrder" placeholder="商户订单号"
                      @change="val=>this.$emit('update:oriMchOrder', val)"
                      :value="oriMchOrder"
            ></el-input>
        </el-form-item>


        <el-form-item label="报关状态" prop="customStatus">
            <enum-select :options="customStatusEnum"
                         :show-empty="true"
                         :selected-id="customStatus"
                         @change="val=>this.$emit('update:customStatus', val)" style="width: 130px;"></enum-select>
        </el-form-item>

        <el-form-item label="报检状态" prop="ciqStatus">
            <enum-select :options="customStatusEnum"
                         :show-empty="true"
                         :selected-id="ciqStatus"
                         @change="val=>this.$emit('update:ciqStatus', val)" style="width: 130px;"></enum-select>
        </el-form-item>


        <el-form-item>
            <el-button type="primary" @click="search">查询</el-button>
        </el-form-item>
        <!--按钮插槽-->
        <slot name="extend-btn"></slot>
    </el-form>


</template>
<script>
    import { EnumSelect, DateRange } from 'bluesdk-element-common'
    import CustomStatusEnum from '../../enum/CustomStatusEnum'

    export default {
        name: 'CustomOrderSearchForm',
        data: function () {
            return {
                customStatusEnum: CustomStatusEnum.map
            }
        },
        props: {
            'outTradeNo': String,
            'payOrderNo': String,
            'oriMchOrder': String,
            'dateBegin': String,
            'dateEnd': String,
            'commonDateRange': String,
            'customStatus': String,
            'ciqStatus': String
        },
        components: {DateRange, EnumSelect},
        methods: {
            search () {
                this.$emit('search')
            }
        }
    }
</script>
