<!--excecl下载弹窗-->
<template>
  <div>
    <p>文件只保存2天，请尽快下载到本地</p>
    <el-table
      :data="list"
      border
      stripe
      max-height="550"
      style="width: 100%">

      <slot name="extendFirst"></slot>

      <el-table-column label="报表类型" header-align="center" align="center"
                       width="150">
        <template slot-scope="scope">
          {{$getObjVal(ReportBizTypeEnum.map, scope.row.bizType)}}
        </template>
      </el-table-column>
      <el-table-column prop="fileDesc" show-overflow-tooltip label="描述" header-align="center" align="left"
      ></el-table-column>
      <el-table-column label="提交时间" header-align="center" align="center"
                       width="165">
        <template slot-scope="scope">
          {{$mTimeToDate(scope.row.createdAt)}}
        </template>
      </el-table-column>
      <el-table-column label="状态" header-align="center" align="center"
                       width="100">
        <template slot-scope="scope">
          {{$getObjVal(TradeStatusEnum.map, scope.row.sucFlag)}}
        </template>
      </el-table-column>
      <el-table-column label="下载" header-align="center" align="center"
                       width="100">
        <template slot-scope="scope">
          <a v-if="scope.row.downUrl" :href="scope.row.downUrl" download="报表.excel">点击下载</a>
        </template>
      </el-table-column>
      <slot name="extendLast"></slot>

    </el-table>
  </div>
</template>
<script>
  import TradeStatusEnum from '../../enum/TradeStatusEnum'
  import ReportBizTypeEnum from '../../enum/ReportBizTypeEnum'

  export default {
    name: 'DownloadListDialog',
    data: function () {
      return {
        TradeStatusEnum,
        ReportBizTypeEnum
      }
    },
    props: {
      list: {
        type: Array,
        default: []
      }
    },
    methods: {}
  }
</script>
<style scoped>
a{
  color: #337ab7;
}
</style>
