<template>
    <entity-table :data="tableData"></entity-table>
</template>
<script>
    import CurrencyMap from '../../enum/CurrencyMap'
    import TradeStatusEnum from '../../enum/TradeStatusEnum'
    import {EntityTable, EntityMixin} from 'bluesdk-element-common'
    import Country from 'bluesdk-country-code'

    export default {
        name: 'ForeignGlobalAccountDetailTable',
        components: {EntityTable},
        mixins: [EntityMixin],
        data () {
            const self = this
            return {
                fieldConfig: {
                    merchantId: '商户号',
                    payChannel: '渠道ID',
                    chnlAccountId: '渠道账户ID',
                    fundAccountId: '钱包ID',
                    nickName: '昵称',
                    currency: {
                        label: '币种',
                        value (entity) {
                            return self.$getObjVal(CurrencyMap, entity.currency)
                        }
                    },
                    countryCode: {
                        label: '地区',
                        value (entity) {
                            return self.$getObjVal(Country, entity.countryCode)
                        }
                    },
                    institutionName: '开户机构',
                    branchCode: '分支机构编号',
                    accountName: '开户名',
                    accountNumber: '收款账号',
                    swiftCode: 'swiftCode',
                    accountRoutingValue: 'accountRoutingValue',
                    accountRoutingType: 'accountRoutingType',
                    swiftSupport: {
                        label: 'swift电汇',
                        value (entity) {
                            return entity.swiftSupport ? '支持' : '不支持'
                        }
                    },
                    localSupport: {
                        label: '本地汇款',
                        value (entity) {
                            return entity.localSupport ? '支持' : '不支持'
                        }
                    },
                    accountStatus: {
                        label: '状态',
                        value (entity) {
                            return TradeStatusEnum.getLabel(entity.accountStatus)
                        }
                    },
                    retCode: '返回状态',
                    retMsg: '返回描述',
                    createdAt: {
                        label: '创建时间',
                        value (entity) {
                            return self.$mTimeToDate(entity.createdAt)
                        }
                    },
                    updatedAt: {
                        label: '更新时间',
                        value (entity) {
                            return self.$mTimeToDate(entity.updatedAt)
                        }
                    }
                }
            }
        }
    }
</script>