<template>
    <el-table
            :data="list"
            border
            style="width: 100%">

        <slot name="extend"></slot>

        <el-table-column prop="outTradeNo" label="订单号" header-align="center" align="center"
                         width="200"></el-table-column>

        <el-table-column label="交易类型" align="center" header-align="center">
            <template slot-scope="scope">
                {{scope.row.currency==='CNY'?'人民币汇款':'外币汇款' }}
            </template>
        </el-table-column>

        <el-table-column label="业务类型" align="center" header-align="center" width="90">
            <template slot-scope="scope">
                {{$getObjVal(foreignBizTypeEnum.map, scope.row.bizType) }}
            </template>
        </el-table-column>

        <el-table-column label="汇款金额" align="right" header-align="center" width="150">
            <template slot-scope="scope">
                {{$moneyFormat(scope.row.remitMoney)}}{{foreignUtil.orderCurrency(scope.row.currency,scope.row.fixMoneyType)}}
            </template>
        </el-table-column>

        <el-table-column label="到账币种" align="center" header-align="center" width="90">
            <template slot-scope="scope">
                {{$getObjVal(currencyMap, scope.row.currency) }}
            </template>
        </el-table-column>

        <el-table-column prop="payeeName" label="收款人" align="left" header-align="center"></el-table-column>
        <el-table-column prop="payeeAccount" label="收款账号" align="left" header-align="center"></el-table-column>

        <el-table-column label="状态" align="center" width="90">
            <template slot-scope="scope">
                {{$getObjVal(tradeStatusEnum.map, scope.row.sucFlag)}}
            </template>
        </el-table-column>

        <el-table-column label="发起时间" align="center" width="155">
            <template slot-scope="scope">
                {{$mTimeToDate(scope.row.createdAt)}}
            </template>
        </el-table-column>

        <el-table-column label="操作" align="center" width="90">
            <template slot-scope="scope">
                <router-link class="el-button el-button--default"
                             :to="{ name: 'foreignPayView', params: { remitId: scope.row.remitId }}">
                    详情
                </router-link>
            </template>
        </el-table-column>

    </el-table>
</template>
<script>
    import TradeStatusEnum from '../../enum/TradeStatusEnum'
    import CurrencyMap from '../../enum/CurrencyMap'
    import ForeignPayTypeEnum from '../../enum/ForeignPayTypeEnum'
    import ForeignBizTypeEnum from '../../enum/ForeignBizTypeEnum'
    import ForeignUtil from '../../util/ForeignUtil'

    export default {
        name: 'ForeignPayOrderListTable',
        data () {
            return {
                tradeStatusEnum: TradeStatusEnum,
                currencyMap: CurrencyMap,
                foreignPayTypeEnum: ForeignPayTypeEnum,
                foreignBizTypeEnum: ForeignBizTypeEnum,
                foreignUtil: ForeignUtil
            }
        },
        props: {
            list: Array
        }
    }
</script>
