<template>
    <el-form :inline="true"  class="query-form">
        <date-range :begin-value="dateBegin"
                    :end-value="dateEnd"
                    :common-range="commonDateRange"
                    @updateBeginValue="val=>this.$emit('update:dateBegin',val)"
                    @updateEndValue="val=>this.$emit('update:dateEnd',val)"
                    @updateCommonRange="val=>this.$emit('update:commonDateRange',val)"
        ></date-range>
        <slot name="extend"></slot>

        <el-form-item label="商户单号" prop="outTradeNo">
            <el-input name="outTradeNo" id="outTradeNo" placeholder="商户单号"
                      @change="val=>this.$emit('update:outTradeNo', val)"
                      :value="outTradeNo"
            ></el-input>
        </el-form-item>

        <el-form-item label="收款人" prop="payeeName">
            <el-input name="payeeName" id="payeeName" placeholder="收款人"
                      @change="val=>this.$emit('update:payeeName', val)"
                      :value="payeeName"
            ></el-input>
        </el-form-item>

        <el-form-item label="收款账号" prop="payeeAccount">
            <el-input name="payeeAccount" id="payeeAccount" placeholder="收款账号"
                      @change="val=>this.$emit('update:payeeAccount', val)"
                      :value="payeeAccount"
            ></el-input>
        </el-form-item>

        <el-form-item label="交易状态" prop="sucFlag">
            <enum-select :options="tradeStatusEnum"
                         :show-empty="true"
                         :selected-id="sucFlag"
                         @change="val=>this.$emit('update:sucFlag', val)" style="width: 130px;"></enum-select>
        </el-form-item>


        <el-form-item>
            <el-button type="primary" @click="search()">查询</el-button>
        </el-form-item>
        <!--按钮插槽-->
        <slot name="extend-btn"></slot>
    </el-form>


</template>
<script>
    import TradeStatusEnum from '../../enum/TradeStatusEnum'
    import {EnumSelect, DateRange} from 'bluesdk-element-common'

    export default {
        name: 'ForeignPaySearchForm',
        data () {
            return {
                tradeStatusEnum: TradeStatusEnum.map
            }
        },
        props: {
            'dateBegin': String,
            'dateEnd': String,
            'commonDateRange': String,
            'outTradeNo': String,
            'sucFlag': String,
            'payeeName': String,
            'payeeAccount': String
        },
        components: {DateRange, EnumSelect},
        methods: {
            search () {
                this.$emit('search')
            }
        }
    }
</script>
