export default {
    isChina: function isChina(countryCode) {
        return countryCode === 'CN';
    },
    businessPicLabel: function businessPicLabel(conuntry) {
        return this.isChina(conuntry) ? '营业执照复印件' : '公司注册证明书复印件';
    },
    idPic1Label: function idPic1Label(conuntry) {
        return this.isChina(conuntry) ? '法人身份证正面照' : '公司法人或负责人护照复印件';
    }
};