export default {
  radioDefaultValue: function radioDefaultValue(array, attribute, cookieValue) {
    if (array.length > 0) {
      if (cookieValue) {
        for (var i in array) {
          if (array[i][attribute].toString() === cookieValue) {
            return cookieValue;
          }
        }
      }
      return array[0][attribute];
    }
    return null;
  }
};