<!--费率列表底部的table-->
<template>
    <div>
        <el-table
                :data="list"
                border
                style="width: 100%">

            <slot name="extend" ></slot>

            <el-table-column prop="merchantId" label="商户ID" header-align="center" align="center"
                             width="200"></el-table-column>
            <el-table-column label="交易类型" header-align="center" align="center">
                <template slot-scope="scope">
                    {{$getObjVal(tradeTypeMap,scope.row.payType)}}
                </template>
            </el-table-column>

            <el-table-column label="支付通道" header-align="center" align="center">
                <template slot-scope="scope">
                    {{$getObjVal(payChanelObj, scope.row.payChannel)}}
                </template>
            </el-table-column>

            <el-table-column label="费率类型" header-align="center" align="center">
                <template slot-scope="scope">
                    {{$getObjVal(PayFeeTypeEnum.map,scope.row.feeType)}}
                </template>
            </el-table-column>

            <el-table-column label="百分比费率（%）" header-align="center" align="center">
                <template slot-scope="scope">
                    {{ PayFeeTypeEnum.PERCENT.value === scope.row.feeType ?
                    $numToPercent(scope.row.feeRate,2,true):'-'}}
                </template>
            </el-table-column>

            <el-table-column label="固定费率（元）" header-align="center" align="center">
                <template slot-scope="scope">
                    {{ PayFeeTypeEnum.FIXED.value === scope.row.feeType ? $moneyFormat(scope.row.feeFixed):'-'}}
                </template>
            </el-table-column>

            <el-table-column label="创建时间" align="center" width="200">
                <template slot-scope="scope">
                    {{$mTimeToDate(scope.row.createdAt)}}
                </template>
            </el-table-column>

            <el-table-column v-if="showOperate" label="操作" align="center" width="90">
                <template slot-scope="scope">
                    <slot name="extend-btn" :row="scope.row"></slot>
                </template>
            </el-table-column>

        </el-table>
    </div>
</template>
<script>
    import PayFeeTypeEnum from '../../enum/PayFeeTypeEnum'
    import PayTypeEnum from '../../enum/PayTypeEnum'

    export default {
        name: 'PayFeeListTable',
        data () {
            return { PayFeeTypeEnum }
        },
        props: {
            tradeTypeMap: {
                type: Object,
                default () {
                    return PayTypeEnum.map
                }
            },
            list: Array,
            payChanelObj: Object, // 支付通道
            showOperate: { // 是否显示最后一栏操作栏
                type: Boolean,
                default: true
            }
        }
    }
</script>
