<!--费率列表顶部的搜索框-->
<template>
    <el-form :inline="true" class="query-form">
        <slot name="extend"></slot>
        <!--<el-form-item label="商户ID" prop="merchantId">-->
        <!--<el-input name="merchantId" id="merchantId" placeholder="商户ID"-->
        <!--@change="val=>this.$emit('update:merchant_id', val)"-->
        <!--:value="merchantId"-->
        <!--&gt;</el-input>-->
        <!--</el-form-item>-->
        <el-form-item label="交易类型" prop="payType">
            <enum-select :options="tradeTypeMap" :show-empty="true" :selected-id="payType"
                         @change="val => this.$emit('update:payType', val)"></enum-select>
        </el-form-item>

        <el-form-item label="支付通道" prop="payChannel">
            <enum-select :options="payChanelObj" :show-empty="true" :selected-id="payChannel"
                         @change="val => this.$emit('update:payChannel', val)"></enum-select>
        </el-form-item>

        <el-form-item>
            <el-button type="primary" @click="search()">查询</el-button>
        </el-form-item>
        <!--按钮插槽-->
        <slot name="extend-btn"></slot>
    </el-form>


</template>
<script>
    import {EnumSelect} from 'bluesdk-element-common'
    import PayTypeEnum from '../../enum/PayTypeEnum'

    export default {
        name: 'PayFeeSearchForm',
        components: {
            EnumSelect
        },
        props: {
            'payChannel': [String, Number],
            'payType': [String, Number],
            'payChanelObj': Object,
            tradeTypeMap: {
                type: Object,
                default () {
                    return PayTypeEnum.map
                }
            },
        },
        methods: {
            search () {
                this.$emit('search')
            }
        }
    }
</script>
