<template>
    <el-table
            :data="list"
            border
            style="width: 100%" >

        <slot name="extend"></slot>

        <el-table-column prop="outTradeNo" label="商户单号" header-align="center" align="left" width="200"></el-table-column>

        <el-table-column prop="payOrderNo" label="支付单号" header-align="center" align="left" width="200"></el-table-column>

        <el-table-column prop="payType" label="支付方式" header-align="center" align="center" >
            <template slot-scope="scope">
                {{$getObjVal(payTypeMap, scope.row.payType)}}
            </template>
        </el-table-column>
        <el-table-column label="金额" align="right" header-align="center" >
            <template slot-scope="scope">
                {{$moneyFormat(scope.row.payMoney)}}￥
            </template>
        </el-table-column>

        <el-table-column prop="payStatus" label="状态" header-align="center" align="center" >
            <template slot-scope="scope">
                {{$getObjVal(payFlagMap, scope.row.payStatus)}}
            </template>
        </el-table-column>

        <el-table-column label="下单时间" align="center" header-align="center"  >
            <template slot-scope="scope">
                {{$mTimeToDate(scope.row.createdAt)}}
            </template>
        </el-table-column>

        <el-table-column label="支付时间" align="center" header-align="center"  >
            <template slot-scope="scope">
                {{scope.row.payStatus?$mTimeToDate(scope.row.payAt):''}}
            </template>
        </el-table-column>


        <el-table-column label="操作" align="center" >
            <template slot-scope="scope">
                <router-link class="el-button el-button--default"
                             :to="{ name: 'payOrderView', params: { payId: scope.row.payId }}">
                    详情
                </router-link>
            </template>
        </el-table-column>

    </el-table>
</template>
<script>
    import PayFlagEnum from '../../enum/PayFlagEnum'
    import PayTypeEnum from '../../enum/PayTypeEnum'

    export default {
        name: 'PayOrderListTable',
        data () {
            return {
                payFlagMap: PayFlagEnum.map,
                payTypeMap: PayTypeEnum.map
            }
        },
        props: {
            list: Array
        }
    }
</script>
