<template>
  <el-table
    :data="list"
    border
    style="width: 100%">

    <slot name="extend"></slot>

    <el-table-column prop="rechargeNo" label="充值流水号" align="center"></el-table-column>

    <el-table-column prop="fundAccountId" label="钱包名称" align="center">
      <template slot-scope="scope">
          {{$getObjVal(fundAccountConfigMap, scope.row.fundAccountId)}}
      </template>
    </el-table-column>

    <el-table-column label="充值金额" align="right" header-align="center">
      <template slot-scope="scope">
        {{$moneyFormat(scope.row.rechargeMoney)}}
      </template>
    </el-table-column>


    <el-table-column label="充值状态" align="center" width="80">
      <template slot-scope="scope">
        {{$getObjVal(TradeStatusEnum.map, scope.row.rechargeStatus)}}
      </template>
    </el-table-column>


    <el-table-column prop="retMsg" label="回执" header-align="center" align="left"></el-table-column>
    <el-table-column label="发起时间" align="center" width="160">
      <template slot-scope="scope">
        {{$mTimeToDate(scope.row.createdAt)}}
      </template>
    </el-table-column>
    <el-table-column label="更新时间" align="center" width="160">
      <template slot-scope="scope">
        {{$mTimeToDate(scope.row.updatedAt)}}
      </template>
    </el-table-column>

    <slot name="extend-right"></slot>
  </el-table>
</template>
<script>
  import TradeStatusEnum from '../../enum/TradeStatusEnum'

  export default {
    name: 'RechargeOrderListTable',
    data () {
      return {
        TradeStatusEnum: TradeStatusEnum
      }
    },
    props: {
      list: Array,
      fundAccountConfigMap: Object
    }
  }
</script>
