<template>
  <el-form :inline="true" class="query-form">
    <date-range :begin-value="dateBegin"
                :end-value="dateEnd"
                :common-range="commonDateRange"
                @updateBeginValue="val=>this.$emit('update:dateBegin',val)"
                @updateEndValue="val=>this.$emit('update:dateEnd',val)"
                @updateCommonRange="val=>this.$emit('update:commonDateRange',val)"
    ></date-range>
    <slot name="extend"></slot>

    <el-form-item label="充值流水号" prop="rechargeNo">
      <el-input name="rechargeNo" id="rechargeNo" placeholder="充值流水号"
                @change="val=>this.$emit('update:rechargeNo', val)"
                :value="rechargeNo"
      ></el-input>
    </el-form-item>


    <el-form-item label="充值状态" prop="rechargeStatus">
      <enum-select :options="rechargeStatusEnum"
                   :show-empty="true"
                   :selected-id="rechargeStatus"
                   @change="val=>this.$emit('update:rechargeStatus', val)" style="width: 130px;"></enum-select>
    </el-form-item>


    <el-form-item>
      <el-button type="primary" @click="search">查询</el-button>
    </el-form-item>
    <!--按钮插槽-->
    <slot name="extend-btn"></slot>
  </el-form>


</template>
<script>
  import {EnumSelect, DateRange} from 'bluesdk-element-common'
  import TradeStatusEnum from '../../enum/TradeStatusEnum'

  export default {
    name: 'RechargeOrderSearchForm',
    data: function () {
      return {
        rechargeStatusEnum: TradeStatusEnum.map
      }
    },
    props: {
      'rechargeNo': String,
      'dateBegin': String,
      'dateEnd': String,
      'commonDateRange': String,
      'rechargeStatus': String
    },
    components: {DateRange, EnumSelect},
    methods: {
      search () {
        this.$emit('search')
      }
    }
  }
</script>
