<template>
  <el-table
          :data="list"
          border
          style="width: 100%">

    <slot name="extend"></slot>

    <el-table-column prop="merchantId" label="商户ID" header-align="center" align="center"
                     width="200"></el-table-column>
    <el-table-column prop="companyName" label="商户名称" header-align="center" align="center"
    ></el-table-column>
    <el-table-column prop="shortName" label="商户简称" header-align="center" align="center"
    ></el-table-column>

    <el-table-column label="创建时间" align="center" width="200">
      <template slot-scope="scope">
        {{$mTimeToDate(scope.row.createdAt)}}
      </template>
    </el-table-column>

    <el-table-column label="操作" align="center" width="220">
      <template slot-scope="scope">
        <router-link class="el-button el-button--default"
                     :to="{name: 'secondMerchantInfoView',query: { merchantId: scope.row.merchantId }}">
          详情
        </router-link>
        <slot name="extend-btn" :row="scope.row"></slot>
      </template>
    </el-table-column>

  </el-table>
</template>
<script>
  export default {
    name: 'SecondMerchantListTable',
    data () {
      return {}
    },
    props: {
      list: Array
    }
  }
</script>
