<template>
  <el-form :inline="true"  class="query-form">
    <slot name="extend"></slot>

    <el-form-item label="商户ID" prop="merchant_id">
      <el-input name="merchant_id" id="merchant_id" placeholder="商户ID"
                @change="val=>this.$emit('update:merchant_id', val)"
                :value="merchantId"
      ></el-input>
    </el-form-item>

    <el-form-item label="商户名称" prop="company_name">
      <el-input name="company_name" id="company_name" placeholder="商户名称"
                @change="val=>this.$emit('update:company_name', val)"
                :value="companyName"
      ></el-input>
    </el-form-item>

    <el-form-item>
      <el-button type="primary" @click="search()">查询</el-button>
    </el-form-item>
    <!--按钮插槽-->
    <slot name="extend-btn"></slot>
  </el-form>


</template>
<script>
  export default {
    name: 'SecondMerchantSearchFrom',
    data () {
      return {
      }
    },
    props: {
      'merchantId': String,
      'companyName': String
    },
    methods: {
      search () {
        this.$emit('search')
      }
    }
  }
</script>
