<template>
  <el-table
    :data="list"
    border
    style="width: 100%">

    <slot name="extend"></slot>

    <el-table-column prop="outWithdrawNo" label="订单号" header-align="center" align="center"
                     width="200"></el-table-column>

    <el-table-column prop="withdrawMoney" label="提现金额(元)" header-align="center" align="right" width="120">
      <template slot-scope="scope">
        {{$moneyFormat(scope.row.withdrawMoney)}}
      </template>
    </el-table-column>

    <el-table-column prop="personName" label="收款人名称" header-align="center" align="left" width="200"></el-table-column>
    <el-table-column prop="cardNo" label="收款账号" header-align="center" align="center" width="200"></el-table-column>
    <el-table-column prop="bankNo" label="开户行" header-align="center" align="center" width="90">
      <template slot-scope="scope">
        {{$getObjVal(bankNoMap, scope.row.bankNo)}}
      </template>
    </el-table-column>
    <el-table-column prop="remark" label="备注" header-align="center" align="left"></el-table-column>
    <el-table-column prop="sucFlag" label="交易状态" header-align="center" align="center" width="90">
      <template slot-scope="scope">
        {{$getObjVal(TradeStatusEnum, scope.row.sucFlag)}}
      </template>
    </el-table-column>

    <el-table-column label="创建时间" align="center" header-align="center" width="200">
      <template slot-scope="scope">
        {{$mTimeToDate(scope.row.createdAt)}}
      </template>
    </el-table-column>

    <el-table-column label="操作" align="center" width="90">
      <template slot-scope="scope">
        <slot name="extend-btn" :row="scope.row"></slot>
      </template>
    </el-table-column>

  </el-table>
</template>
<script>
  import TradeStatusEnum from '../../enum/TradeStatusEnum'
  import BankAccountTypeEnum from '../../enum/BankAccountTypeEnum'
  import bankNoMap from 'bluesdk-bank-no'

  export default {
    name: 'WithdrawListTable',
    data () {
      return {
        bankNoMap,
        TradeStatusEnum: TradeStatusEnum.map,
        BankAccountTypeEnum: BankAccountTypeEnum.map
      }
    },
    props: {
      list: Array
    }
  }
</script>
